import React, { useState, useEffect } from 'react';
import './App.css';
import words from './data/words';

function App() {
  const [currentWord, setCurrentWord] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [showAnswer, setShowAnswer] = useState(false);

  const nextWord = (category) => {
    const wordList = words[category];
    const randomIndex = Math.floor(Math.random() * wordList.length);
    setCurrentWord(wordList[randomIndex]);
    setShowAnswer(false);
  };

  const handleSelectCategory = (category) => {
    setSelectedCategory(category);
    nextWord(category);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>日文單字練習</h1>
      </header>
      <main>
        <div className="category-selector">
          <h2>請選擇題庫：</h2>
          {Object.keys(words).map(category => (
            <button key={category} onClick={() => handleSelectCategory(category)}>
              {category}
            </button>
          ))}
        </div>

        {selectedCategory && (
          <div className="quiz-area">
            <div className="kanji-display">
              {currentWord ? currentWord.kanji : '請選擇題庫'}
            </div>

            {currentWord && (
              <div className="controls">
                <button onClick={() => setShowAnswer(true)}>查看答案</button>
                {showAnswer && <div className="kana-display">{currentWord.kana}</div>}
              </div>
            )}

            {showAnswer && (
              <div className="feedback-buttons">
                <button className='correct'>答對了！</button>
                <button className='incorrect'>答錯了</button>
              </div>
            )}

            {currentWord && <button className='next-word' onClick={() => nextWord(selectedCategory)}>下一題</button>}
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
